const bel = require('bel')

function crystalIsland({date, info}, deco, island, css, title) {
    let el = bel`
                <div class=${css.scene}>
                    <div class=${css.deco}>
                        <div class=${css.content}>
                            <h3>${date}</h3>
                            ${ info === 'Coming soon' ? bel`<h3>${info}</h3>` : bel`<p>${info}</p>` }
                        </div>
                        ${deco.map(item => item)}
                    </div>
                    ${title}
                    ${island}
                </div>
                `
    return el
}

module.exports = crystalIsland