const bel = require('bel')
const csjs = require('csjs-inject')
// Widgets
const graphic = require('graphic')
const Rellax = require('rellax')
const Content = require('content')

async function editor (data) {
    const css = styles
    const graphics = [
      graphic(css.island, './src/node_modules/assets/svg/floating-island.svg'),
      graphic(css.energyIsland, './src/node_modules/assets/svg/energy-island.svg'),
      graphic(css.tree, './src/node_modules/assets/svg/single-tree.svg'),
      graphic(css.stone, './src/node_modules/assets/svg/stone.svg'),
      graphic(css.cloud1, './src/node_modules/assets/svg/cloud.svg'),
      graphic(css.cloud2, './src/node_modules/assets/svg/cloud.svg'),
      graphic(css.cloud3, './src/node_modules/assets/svg/cloud.svg'),
      graphic(css.cloud4, './src/node_modules/assets/svg/cloud.svg'),
      graphic(css.cloud5, './src/node_modules/assets/svg/cloud.svg'),
    ]

    const [island, energyIsland, tree, stone, cloud1, cloud2, cloud3, cloud4, cloud5] = await Promise.all(graphics)

    // Parallax effects
    let cloud1Rellax = new Rellax( cloud1, { speed: 2})
    let cloud2Rellax = new Rellax( cloud2, { speed: 3})
    let cloud3Rellax = new Rellax( cloud3, { speed: 4})
    let cloud4Rellax = new Rellax( cloud4, { speed: 4})
    let cloud5Rellax = new Rellax( cloud5, { speed: 3})

    let el = bel`
    <section id="smartcontractUI" class="${css.section}">
        ${Content(data, css)}
        
        <div class=${css.scene}>
            <div class=${css.objects}>
                <img class=${css.logo} src=${data.logo} alt="${data.title} logo">
                <img class=${css.screenshot} src=${data.image} alt=${data.title}>
                <div class=${css.deco}>
                    ${stone}
                    ${tree}
                </div>
            </div>
            ${island}
        </div>
        ${energyIsland}
        ${cloud1}
        ${cloud2}
        ${cloud3}
        ${cloud4}
        ${cloud5}
    </section>
    `
    return el
}

const styles = csjs`
.section {
    position: relative;
    display: grid;
    grid-template-rows: auto 1fr;
    grid-template-columns: 40% 60%;
    background-image: linear-gradient(0deg, var(--section2BgGEnd), var(--section2BgGStart));
    padding: 5vw 2vw;
}
.content {
    position: relative;
    z-index: 9;
    grid-row-start: 1;
    grid-column-start: 1;
    grid-column-end: 2;
    text-align: center;
    padding: 0 5%;
    margin-bottom: 86px;
}
.subTitleColor {
    color: var(--section2TitleColor);
}
.buttonBg {
    background-image: linear-gradient(0deg, #4dc7be, #35bdb9);
}
.scene {
    position: relative;
    grid-row-start: span 2;
    grid-column-start: 2;
}
.objects {
    position: relative;
}
.screenshot {
    width: 80%;
    margin-bottom: -5.5%;
    margin-left: 10%;
}
.logo {
    position: absolute;
    left: 0%;
    bottom: -20%;
    width: 20%;
}
.deco {
    position: absolute;
    right: 0;
    bottom: -18.5%;
    width: 100%;
    display: flex;
    align-items: flex-end;
    justify-content: flex-end;
}
.tree {
    width: 13%;
}
.stone {
    position: relative;
    width: 10%;
    right: -3%;
}
.island {
}
.energyIsland {
    grid-row-start: 2;
    grid-column-start: 1;
    grid-column-end: 2;
    width: 80%;
    justify-self: center;
}
.cloud1 {
    position: absolute;
    width: 10vw;
    left: 2vw;
    bottom: 0;
    z-index: 3;
}
.cloud2 {
    position: absolute;
    width: 15vw;
    left: 38vw;
    bottom: -35vw;
    z-index: 2;
}
.cloud3 {
    position: absolute;
    width: 8vw;
    right: 30vw;
    bottom: -34vw;
    z-index: 3;
}
.cloud4 {
    position: absolute;
    width: 14vw;
    right: 6vw;
    bottom: -40vw;
    z-index: 3;
}
.cloud5 {
    position: absolute;
    width: 8vw;
    right: 2vw;
    bottom: -10vw;
    z-index: 2;
}
@media only screen and (max-width: 1024px) {
    .content {
        grid-row-start: 1;
        grid-column-end: 3;
    }
    .scene {
        grid-row-start: 2;
    }
    .energyIsland {
        align-self: end;
    }
}

@media only screen and (max-width: 640px) {
    .scene {
        grid-column-start: 1;
        grid-column-end: 3;
    }
    .energyIsland {
        grid-row-start: 3;
        grid-column-start: 1;
        grid-column-end: 3;
        width: 60%;
        justify-self: start;
    }
    .cloud1 {
        width: 16vw;
    }
    .cloud2 {
        width: 20vw;
        left: 50vw;
        bottom: 10vw;
    }
    .cloud3 {
        width: 15vw;
        bottom: 50vw;
    }
    .cloud4 {
        width: 25vw;
        bottom: -85vw;
    }
    .cloud5 {
        width: 15vw;
        bottom: 30vw;
    }
}
`

module.exports = editor