module.exports = parallax

function parallax (item, input, speed){
    if (!item) throw new Error(`Not found element: ${item}`)

    let el = document.querySelector(item)
    if (input === 'x') {
        el.style.transform = `translateX( ${(window.pageYOffset / speed)}px )`
    } else if (input === 'y') {
        el.style.transform = `translateY( ${(window.pageYOffset / speed)}px )`
    } else {
        throw new Error(`expect direction is x or y: ${input}`)
    }
    
} 
